#include <stdlib.h>

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k); 

    int *arr = (int*)malloc(n * sizeof(int)); if (!arr) {
