#include <stdlib.h>
#include <string.h>

struct adj_list {
    int *edges;
    int size;
    int cap;
};

void add_edge(struct adj_list *g, int u, int v) {
    if (g[u].size >= g[u].cap) {
        // need to expand
        if (g[u].cap == 0) {
            g[u].cap = 1;
            g[u].edges = malloc(g[u].cap * sizeof(int));
        } else {
            g[u].cap *= 2;
            g[u].edges = realloc(g[u].edges, g[u].cap * sizeof(int));
        }
    }
    g[u].edges[g[u].size++] = v;
}

int dfs(struct adj_list *g, int x, int *vis, int n) {
    typedef struct {
        int u;
        int col;
    } StackNode;

    StackNode stack[n]; // requires C99 VLA
    int top = -1;
    int flag = 1;

    stack[++top] = (StackNode){x, 1};

    while (top >= 0) {
        StackNode curr = stack[top];
        top--;
        int u = curr.u;
        int col = curr.col;

        if (vis[u]) {
            if (vis[u] ! = col) {
                flag = 0;
            }
            continue;
        }

        vis[u] = col;
        for (int i = 0; i < g[u].size; ++i) {
            int v = g[u].edges[i];
            stack[++top] = (StackNode){v, 3 - col};
        }
    }

    return flag;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    struct adj_list *g = malloc( (n + 1) * sizeof(struct adj_list));

    for (int i = 0; i <= n; i++) {
        g[i].edges = NULL;
        g[i].size = 0;
        g[i].cap = 0;
    }

    for (int i =0; i<m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        add_edge(g, u, v);
        add_edge(g, v, u);
    }

    int vis[n+1];
    memset(vis, 0, (n+1)*sizeof(int));

    int ci=0, cb=0, cc=0;

    for (int i=1; i<=n; i++) {
        if (vis[i] ==0) {
            // not visited
            if (g[i].size ==0) {
                ci++;
            } else {
                int res = dfs(g, i, vis, n); if (res) {
                    cb++;
                } else {
                    cc++;
                }
            }
        }
    }

    // compute the result
    long long total = (long long)ci * ci;
    total += 2LL * ci * (n - ci);
    total += (long long)cc * cc;
    total += 2LL * cb * cc;
    total += 2LL * cb * cb;

    printf("%lld\n", total);

    // cleanup

    // Free memory for each edges:
    for (int i=1; i<=n; ++i){
        free(g[i].edges);
    }
    free(g);

    return 0;
}

