#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Define maximum possible n. Let's assume n up to 1e5. #define MAX_N 100005
#define MAX_EDGES 100005

struct Edge {
    int a;
    int b;
};

struct Edge edges[MAX_EDGES];

int **g;
int *g_size;

// Function prototypes
int maxMatching(int v, int p);

int main() {
    int n;
    scanf("%d", &n);

    // Read edges
    int edge_count = n-1;
    for (int i = 0; i < edge_count; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        a--;
        b--;
        edges[i].a = a;
        edges[i].b = b;
    }

    // Count the number of edges per node
    int count[MAX_N] = {0};
    for (int i = 0; i < edge_count; i++) {
        count[edges[i].a]++;
        count[edges[i].b]++;
    }

    // Allocate adjacency lists
    g = (int **)malloc(n * sizeof(int *));
    g_size = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        g[i] = (int *)malloc(count[i] * sizeof(int));
        g_size[i] = count[i];
        count[i] = 0; // reset to use as index
    }

    // Fill adjacency lists
    for (int i = 0; i < edge_count; i++) {
        int a = edges[i].a;
        int b = edges[i].b;
        g[a][count[a]++] = b;
        g[b][count[b]++] = a;
    }

    // Compute maxMatching
    int root = 0;
    int total = maxMatching(root, -1);
    int m = total / 2;

    // Determine first's output
    char first_result[10];
    if (2 * m == n) {
        strcpy(first_result, "Second");
    } else {
        strcpy(first_result, "First");
    }

    // Second's processing: check if first_result contains both 'A' and 'B'
    int hasA = 0, hasB = 0;
    for (int i = 0; first_result[i] ! = '\0'; i++) {
        if (first_result[i] == 'A') hasA = 1;
        if (first_result[i] == 'B') hasB = 1;
    }

    if (hasA && hasB) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }

    // Free memory
    for (int i = 0; i < n; i++) {
        free(g[i]);
    }
    free(g);
    free(g_size);

    return 0;
}

