    int a = 0, o = 0;
    for (int i = 0; i < g_size[v]; i++) {
        int w = g[v][i];
        if (w == p) continue;
        int r = maxMatching(w, v);
        a += r / 2;
        o += r % 2;
    }
    int min1o = (o < 1) ? o : 1;
    return 2 * a + min1o + 1;
