#include <stdio.h>
#include <string.h>

int main() {
    int n, a, b, c; scanf("%d %d % d % d", &n, &a, &b, &c);

    // First part: compute dp[n]
    const int maxn = 4010;
    int dp[maxn];
    for (int i = 0; i < maxn; i++) {
        dp[i] = -2147483648;
    }
    dp[0] = 0;
    int arr[3] = {a, b, c};
    for (int i = 0; i < 3; i++) {
        int coin = arr[i];
        for (int j = coin; j < maxn; j++) {
            if (dp[j - coin] + 1 > dp[j]) {
                dp[j] = dp[j - coin] + 1;
            }
        }
    }
    int result = dp[n];

    // Convert result to string
    char s[20];
    sprintf(s, "%d", result);
    int N = strlen(s);

    // Second part: process the string
    int mod_arr[N];
    int mod_cnt[2019] = {0};
    int radix = 1;
    for (int i = 0; i < N; i++) {
        int pos = N - 1 - i;
        int digit = s[pos] - '0';
        int tmp = (digit * radix) % 2019; if (i != 0) {
            tmp = (tmp + mod_arr[i - 1]) % 2019;
