#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    // Read first part's inputs
    int n, m;
    scanf("%d %d", &n, &m);

    // Read m lists
    int **lists = malloc(m * sizeof(int*));
    int *list_sizes = malloc(m * sizeof(int));
    for (int i = 0; i < m; i++) {
        int count;
        scanf("%d", &count);
        lists[i] = malloc(count * sizeof(int));
        for (int j = 0; j < count; j++) {
            scanf("%d", &lists[i][j]);
        }
        list_sizes[i] = count;
    }

    // Read p array
    int *p = malloc(m * sizeof(int));
    for (int i = 0; i < m; i++) {
        scanf("%d", &p[i]);
    }

    // Compute q_elements
    bool *q_present = calloc(n + 1, sizeof(bool)); // Initialize to false
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < list_sizes[i]; j++) {
            int elem = lists[i][j];
            q_present[elem] = true;
        }
    }

    // Collect q_elements and their positions
    int k = 0;
    for (int x = 1; x <= n; x++) {
        if (q_present[x]) k++;
    }
    int *q_elements = malloc(k * sizeof(int));
    int *pos_in_q = malloc((n + 1) * sizeof(int)); // pos_in_q[x] is index in q_elements or -1
    for (int x = 0; x <= n; x++) pos_in_q[x] = -1;
    int idx = 0;
    for (int x = 1; x <= n; x++) {
        if (q_present[x]) {
            q_elements[idx] = x;
            pos_in_q[x] = idx++;
        }
    }

    // Precompute list_masks
    int *list_masks = malloc(m * sizeof(int));
    for (int i = 0; i < m; i++) {
        list_masks[i] = 0;
        for (int j = 0; j < list_sizes[i]; j++) {
            int elem = lists[i][j];
            int pos = pos_in_q[elem];
            if (pos != -1) {
                list_masks[i] |= (1 << pos);
            }
        }
    }

    // Compute count of valid subsets
    int count = 0;
    for (int subset_mask = 0; subset_mask < (1 << k); subset_mask++) {
        bool valid = true;
        for (int list_idx = 0; list_idx < m; list_idx++) {
            int list_mask = list_masks[list_idx];
            int w = p[list_idx];
            int intersect = subset_mask & list_mask;
            int cnt = 0;
            while (intersect) {
                cnt += intersect & 1;
                intersect >>= 1;
            }
            if (cnt % 2 != w) {
                valid = false;
                break;
            }
        } if (valid) {
            count++;
        }
    }

    // Compute ans
    int exponent = n - k;
    long long ans = (long long)count * (1LL << exponent);

    // Process second part
    char ans_str[20];
    sprintf(ans_str, "%lld", ans); // Use %lld for long long

    // Count frequencies
    int freq[256] = {0};
    int len = strlen(ans_str);
    for (int i = 0; i < len; i++) {
        char c = ans_str[i];
        freq[c]++;
    }

    int odd_count = 0;
    for (int c = 0; c < 256; c++) {
        if (freq[c] % 2 != 0) {
            odd_count++;
        }
    }

    if (odd_count <= 1) {
        printf("First\n");
    } else {
        if (len % 2 == 0) {
            printf("Second\n");
        } else {
            printf("First\n");
        }
    }

    // Free allocated memory
    for (int i = 0; i < m; i++) {
        free(lists[i]);
    }
    free(lists);
    free(list_sizes);
    free(p);
    free(q_present);
    free(q_elements);
    free(pos_in_q);
    free(list_masks);

    return 0;
