int first_step(int N, char *S) {

    int r =0, g=0, b=0;

    for(int i=0; i<N; i++) {

        if (S[i] == 'R') r++;

        else if (S[i] == 'G') g++;

        else if (S[i] == 'B') b++;

    }

    int total = r * g * b;

    int sum =0;

    for (int step=1; step <= (N+1)/2; step++) { // ceil(n/2) → (n+1)/2

        for (int i=0; i < N - 2*step; i++) {

            char a = S[i];

