#include <stdio.h>
#include <stdlib.h>

int main() {

   int n, m, x; scanf("%d %d %d", &n, &m, &x);

   // Read the c array.

   int c[n][m+1]; // assuming n is known at compile time, but in C99 VLAs are allowed. for (int i=0; i <n; i++) {

      for (int j=0; j < m+1; j++) {

         scanf("%d", &c[i][j]);

      }

   }

   // Now process the subsets. int a = -1; // default to -1 in case no subsets remain. // Generate initial subsets. int total_subsets = (1 << n) -1;

   int *current_subsets = malloc(total_subsets * sizeof(int));

   int current_size =0;

   for (int mask=1; mask < (1<<n); mask++) {

      current_subsets[current_size++] = mask;

   }

   int valid =1;

   for (int i_subject=1; i_subject <=m; i_subject++) {

      // Process subject i_subject (columns 1-based from 1 to m). int next_size =0;

      int *next_subsets = malloc(current_size * sizeof(int));

      for (int k=0; k < current_size; k++) {

         int mask = current_subsets[k];

         int sum =0;

         for (int j=0; j <n; j++) {

            if (mask & (1<<j)) {

               sum += c[j][i_subject]; // since columns 1 to m are 1-based in the problem.

            }

         } if (sum >=x) {

            next_subsets[next_size++] = mask;

         }

      }

      free(current_subsets);

      current_subsets = next_subsets;

      current_size = next_size;

      if (current_size ==0) {

         valid =0;

         break;

      }

   }

   if (valid) {

      // Find the minimal cost. a = INT_MAX;

      for (int k=0; k < current_size; k++) {

         int mask = current_subsets[k];

         int cost =0;

         for (int j=0; j <n; j++) {

            if (mask & (1<<j)) {

               cost += c[j][0];

            }

         }

         if (cost < a) {

            a = cost;

         }

      }

   }

   // Now, process File2 and File3. int b = (a ==0) ? 1 :0;

   int c_val = (2*b +1)/3;

   printf("%d\n", c_val);

   free(current_subsets);

   return 0;

