#include <stdio.h>
#include <stdlib.h>

int check(int n, int* x, int* y, int* h, int cx, int cy) {
    int ch = -1;
    for (int i = 0; i < n; i++) {
        if (h[i] > 0) {
            ch = abs(x[i] - cx) + abs(y[i] - cy) + h[i];
            break;
        }
    }
    for (int i = 0; i < n; i++) {
        int expected = ch - abs(x[i] - cx) - abs(y[i] - cy);
        if (expected < 0) expected = 0; if (h[i] != expected) {
            return -1;
        }
    }
    return ch;
}

int main() {
    int n;
    scanf("%d", &n);
    int* x = (int*)malloc(n * sizeof(int));
    int* y = (int*)malloc(n * sizeof(int));
    int* h = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) { scanf("%d % d % d", &x[i], &y[i], &h[i]);
    }
    for (int cx = 0; cx <= 100; cx++) {
        for (int cy = 0; cy <= 100; cy++) {
            int ch = check(n, x, y, h, cx, cy);
            if (ch > 0) {
                printf("%d %d %d\n", cx, cy, ch);
                free(x);
                free(y);
                free(h) ;
                return 0;
            }
        }
    }
    // According to problem statement, there's always a solution, so this part may not be reached. free(x);
    free(y);
    free(h); return 0;
} Wait, but in the original Java code, they loop through all possible cx and cy, and for each, check and print if ch is positive. But the Java code doesn't return after finding the first solution.

