        {s0, s1, s2, s3},

        {s1, s0, s2, s3},

        {s0, s2, s1, s3},

        {s0, s1, s3, s2}

    };

    for (int i = 0; i <4; ++i) {

        int a0 = cases[i][0];

        int a1 = cases[i][1];

        int a2 = cases[i][2];

        int a3 = cases[i][3];

        if (a0 ==0 && a1 ==2 && a2 ==1) return 0;

        if (a1 ==0 && a2 ==2 && a3 ==1) return 0;

