            int s0 = state /16; int s1 = (state /4) %4;

            int s2 = state %4;

            for (int c=0; c<4; ++c) {

                if (is_valid(s0, s1, s2, c)) {

                    int new_state = (s1 *4 + s2) *4 + c; dp[curr][new_state] = (dp[curr][new_state] + dp[prev][state]) % MOD;

                }

            }

        }

        prev = curr;

    }

    long long ans =0;

    for (int state =0; state <64; ++state) {

        ans = (ans + dp[prev][state]) % MOD;

    }

    printf("%lld\n", ans);

