#include <stdio.h>
#include <stdlib.h>

int compare(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

int main() {
    int n;
    scanf("%d", &n);
    int size = 3 * n;
    int *array = (int *)malloc(size * sizeof(int));
    for (int i = 0; i < size; i++) {
        scanf("%d", &array[i]);
    }
    qsort(array, size, sizeof(int), compare);
    long long res = 0;
    int head = size - 2;
    for (int i = 0; i < n; i++) {
        res += array[head];
        head -= 2;
    }
    printf("%lld\n", res);
    free(array);
    return 0;
} Wait, let's check. The compare function for qsort should sort in ascending order.

