void printIsland(int n, int count) {
    int a = (n + 1)/2;
    int b = n / 2;
    int max = a*a + b*b;
    if (count > max) {
        printf("NO\n");
        return;
    }
    printf("YES\n");
    // Now generate the grid
    for (int i = 0; i < n; i++) {
