#include <stdlib.h>

int compare(const void *a, const void *b) {
    int x = *(const int *)a; int y = *(const int *)b;
    return x - y;
}

int main() {
    int n;
    scanf("%d", &n);
    int list[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &list[i]);
    }
    qsort(list, n, sizeof(int), compare);
    int c = 1;
