#include <stdio.h>
#include <stdlib.h>  // for abs()

int gcd(int a, int b) {
    a = abs(a);
    b = abs(b);
    while (b != 0) {
        int temp = a % b;
        a = b;
        b = temp;
    }
    return a;
}

int main() {
    int N;
    scanf("%d", &N);
    int A[N];
    for (int i = 0; i < N; ++i) {
        scanf("%d", &A[i]);
    }
    int result = A[0];
    for (int i = 1; i < N; ++i) {
        result = gcd(result, A[i]);
        if (result == 1) {
            break;  // GCD can't get lower than 1
        }
    }
    printf("%d\n", result);
    return 0;
}

