#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <ctype.h>

// Function to compare integers for qsort
int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    char buffer[1000000]; // Assuming a large enough buffer for the input line
    int *a = NULL;
    int size = 0;

    // Read and ignore the first line
    fgets(buffer, sizeof(buffer), stdin);

    // Read the second line containing the array
    fgets(buffer, sizeof(buffer), stdin);

    // Split the line into tokens
    char *token = strtok(buffer, " \n");
    while (token != NULL) {
        // Convert token to integer
        int num = atoi(token);
        // Allocate memory for the new element
        int *temp = realloc(a, (size + 1) * sizeof(int)); if (temp == NULL) {
            // Handle allocation failure
            free(a);
            return 1;
        }
        a = temp;
        a[size++] = num;
        token = strtok(NULL, " \n");
    }

    // Sort the array
    qsort(a, size, sizeof(int), compare);

    // Check adjacent elements
    for (int i = 1; i < size; i++) {
        if (a[i] != a[i-1] && a[i] < 2 * a[i-1]) {
            printf("YES\n");
            free(a);
            return 0;
        }
    }

    printf("NO\n");
    free(a);
    return 0;
}

