      #include <stdio.h>
      #include <stdlib.h>
      #include <math.h>

      typedef struct {
          int value;
          int index;
      } Element;

      int compare_element(const void *a, const void *b) {
          Element *ea = (Element*)a;
          Element *eb = (Element*)b;
          if (ea->value != eb->value) {
              return ea->value - eb->value;
          }
          return eb->index - ea->index;   // for the same value, descending by index.
      }

      int main() {
          // Part 1: read input for the first program. int N;
          scanf("%d", &N); Element *arr = (Element*)malloc(N * sizeof(Element));
          for (int i=0; i<N; i++) {
              scanf("%d", &arr[i].value);
              arr[i].index = i;
          }

          // Sort by value and by index (descending for same value)
          qsort(arr, N, sizeof(Element), compare_element);

          // Create index_to_use: for each position in the sorted array, the index to use is the last_occurrence (largest index) of that value. int *index_to_use = (int*)malloc(N * sizeof(int));
          for (int i=0; i<N; ) {
