      #include <stdio.h>
      #include <stdlib.h>
      #include <math.h>

      typedef struct {
          int value;
          int index;
      } Element;

      int compare_element(const void *a, const void *b) {
          Element *ea = (Element*)a;
          Element *eb = (Element*)b;
          if (ea->value != eb->value) {
              return ea->value - eb->value;
          }
          return eb->index - ea->index;   // for the same value, descending by index.
      }

      int main() {
          // Part 1: read input for the first program.
          int N;
          scanf("%d", &N);
          Element *arr = (Element*)malloc(N * sizeof(Element));
          for (int i=0; i<N; i++) {
              scanf("%d", &arr[i].value);
              arr[i].index = i;
          }

          // Sort by value and by index (descending for same value)
          qsort(arr, N, sizeof(Element), compare_element);

          // Create index_to_use: for each position in the sorted array, the index to use is the last_occurrence (largest index) of that value.
          int *index_to_use = (int*)malloc(N * sizeof(int));
          for (int i=0; i<N; ) {
              int j = i;
              while (j < N && arr[j].value == arr[i].value) {
                  j++;
              }
              int last_index = arr[i].index;   // the first in the group has the largest index.
              for (int k=i; k<j; k++) {
                  index_to_use[k] = last_index;
              }
              i = j;
          }

          int bin = 0;
          int diff = 0;
          for (int i=0; i<N; i++) {
              int idx = index_to_use[i];
              if ((bin % 2) != (idx % 2)) {
                  diff++;
              }
              bin = (bin+1) % 2;
          }
          int ans1 = diff / 2;

          free(arr);
          free(index_to_use);

          // Part 2: 
          long n_val = (long)ans1;
          int answer2 = 0;
          if (n_val > 1) {
              // Compute the integer square root of n_val.
              int sqrt_n = (int)sqrt(n_val);
              for (int i=2; i<=sqrt_n; i++) {
                  int count = 0;
                  while (n_val % i == 0) {
                      count++;
                      n_val /= i;
                  }
                  for (int j=1; j<=count; j++) {
                      count -= j;
                      answer2++;
                  }
              }
              if (n_val > 1) {
                  answer2++;
              }
          }
          printf("%d\n", answer2);

          return 0;
      }

