        // DFS function for the first program
        int dfs(int child, int par, int** graph, int* deg, char* seen, int* count, int cycle, int path) {
            if (seen[child]) {
                return cycle;
            }
            seen[child] = 1;
            for (int j = 0; j < deg[child]; j++) { int i = graph[child][j];
                if (i == par) continue;
                (*count)++;
                if (dfs(i, child, graph, deg, seen, count, cycle, path) == cycle) {
                    return cycle;
                }
            }
            return path;
        }

