            // Arrays for edges
            int* edges_x = NULL;
            int* edges_y = NULL;
            if (n > 0) {
                edges_x = (int*)malloc(n * sizeof(int));
                edges_y = (int*)malloc(n * sizeof(int));
            }

            // Degree array for nodes 1..m
            int* deg = (int*)calloc(m+1, sizeof(int)); // index0 unused

            // Read the edges and compute degrees
            for (int i=0; i<n; i++) {
                scanf("%d %d", &edges_x[i], &edges_y[i]);
                deg[edges_x[i]]++;
                deg[edges_y[i]]++;
            }

            // Build the graph: adjacency list for nodes 1..m
            int** graph = (int**)malloc((m+1) * sizeof(int*));
            // Total storage for edges: 2*n
            int* storage = NULL;
            if (2*n > 0) {
                storage = (int*)malloc(2 * n * sizeof(int));
            }
            // Also, we need to reset an array for current index per node for filling
            int* curr_index = (int*)calloc(m+1, sizeof(int));

            // Set the pointers for graph[i]
            if (storage) {
                int pos = 0;
                for (int i=1; i<=m; i++) {
                    graph[i] = storage + pos;
                    pos += deg[i];
                }
            } else {
                for (int i=1; i<=m; i++) {
                    graph[i] = NULL;
                }
            }

            // Fill the graph
            for (int i=0; i<n; i++) {
                int x = edges_x[i];
                int y = edges_y[i];
                if (deg[x] > 0) {
                    graph[x][curr_index[x]] = y;
                    curr_index[x]++;
                }
                if (deg[y] > 0) {
                    graph[y][curr_index[y]] = x;
                    curr_index[y]++;
                }
            }

            // Free edges arrays if we allocated
            if (n>0) {
                free(edges_x);
                free(edges_y);
            }
            free(curr_index);

            // Seen array for DFS
            char* seen = (char*)calloc(m+1, sizeof(char));
            int toremove = 0;
            const int cycle_flag = 1;
            const int path_flag = 0;

            // Traverse each node
            for (int i=1; i<=m; i++) {
                if (!seen[i]) {
                    int count = 0;
                    int result = dfs(i, 0, graph, deg, seen, &count, cycle_flag, path_flag);
                    if (result == cycle_flag) {
                        if (count % 2 == 1) {
                            toremove++;
                        }
                    }
                }
            }

            if ((m - toremove) % 2 == 1) {
                toremove++;
            }

            // Now, toremove is the output of the first program.

            // Step 2: call second program
            int c = second_program(toremove);

            // Step 3: convert to string and call third program
            char buffer[20];
            sprintf(buffer, "%d", c);
            const char* ans = third_program(buffer);
            printf("%s\n", ans);

            // Free memory
            free(seen);
            free(deg);
            if (storage) free(storage);
            free(graph);

            return 0;
