            #include <stdio.h>
            #include <stdlib.h>
            #include <string.h>
            #include <limits.h>

            #define MAXN 300000
            #define INF (INT_MAX)

            int n;
            int tree[4 * MAXN];

            int min(int a, int b) {
                return a < b ? a : b;
            }

            void build(int idx, int l, int r) {
                if (l == r) {
                    tree[idx] = l;   // initially available: the value is the index
                    return;
                }
                int mid = (l + r) / 2;
                build(2*idx+1, l, mid);
                build(2*idx+2, mid+1, r);
                tree[idx] = min(tree[2*idx+1], tree[2*idx+2]);
            }

            void update(int idx, int l, int r, int pos, int val) {
                if (l == r) {
                    tree[idx] = val;
                    return;
                }
                int mid = (l + r) / 2;
                if (pos <= mid) {
                    update(2*idx+1, l, mid, pos, val);
                } else {
                    update(2*idx+2, mid+1, r, pos, val);
                }
                tree[idx] = min(tree[2*idx+1], tree[2*idx+2]);
            }

            int query(int idx, int l, int r, int ql, int qr) {
                if (ql > r || qr < l) {
                    return INF;
                } if (ql <= l && r <= qr) {
                    return tree[idx];
                }
                int mid = (l + r) / 2;
                int left_val = query(2*idx+1, l, mid, ql, qr);
                int right_val = query(2*idx+2, mid+1, r, ql, qr);
                return min(left_val, right_val);
