    for (int i = 0; i < N && count < K; i++) {
        if (flag && c1[i] == 'R') {
            flag = 0;
            count++;
        }
        if (count >= K) break;
        if (c1[i] == 'L') {
            c1[i] = 'R';
            flag = 1;
