        a -= t*b; // a is a mod b now?
        tmp = a; 
        a = b; 
        b = tmp; // swap a and b: now a becomes b, and b is the previous a-t*b (temporary?)

        u -= t*v; // compute new u
        tmp =u; 
        u = v; 
        v = tmp; 
    }

