            if (i < len_s - 1 && s[i + 1] == 'C') {
                t[t_length++] = 'D';
                i += 2;
            } else {
                t[t_length++] = 'X';
                i++;
            }
        } else {
            t[t_length++] = 'X';
            i++; }
    }
    t[t_length] = '\0'; // null-terminate t

    int total = 0;
    int numA = 0;
    for (int j = 0; j < t_length; j++) {
        if (t[j] == 'X') {
            numA = 0;
        } else if (t[j] == 'A') {
            numA += 1;
        } else {
            // must be 'D'
            total += numA;
        }
    }
    printf("%d\n", total);
}

