#include <stdio.h>
#include <string.h>

void solve() {
    char s[200000];
    fgets(s, sizeof(s), stdin);
    size_t len_s = strlen(s);
    if (len_s > 0 && s[len_s - 1] == '\n') {
        s[len_s - 1] = '\0';
        len_s--;
    }

    char t[200000];
    int t_length = 0;
    int i = 0;
    while (i < len_s) {
        if (s[i] == 'A') {
            t[t_length++] = 'A';
            i++;
        } else if (s[i] == 'B') {
            if (i < len_s - 1 && s[i + 1] == 'C') {
                t[t_length++] = 'D';
                i += 2;
            } else {
                t[t_length++] = 'X';
                i++;
            }
        } else {
            t[t_length++] = 'X';
            i++;
        }
    }
    t[t_length] = '\0'; // null-terminate t

    int total = 0;
    int numA = 0;
