#include <stdio.h>
#include <string.h>

typedef struct {
    int num;
    int red;
} Box;

void move(Box *src, Box *dst) {
    if (src->num == 0) return;
    else if (src->num == 1) {
        src->num = 0;
        dst->num++;
        if (src->red) {
            dst->red = 1;
        }
        src->red = 0;
    } else {
        src->num--;
        dst->num++;
        if (src->red) {
            dst->red = 1;
        }
    }
}

int main() {
    int N, M;
    scanf("%d %d", &N, &M);

    Box B[N];
    B[0].num = 1;
    B[0].red = 1;
    for (int i = 1; i < N; i++) {
        B[i].num = 1;
        B[i].red = 0;
    }

    for (int i = 0; i < M; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        x--;
        y--;
        move(&B[x], &B[y]);
    }

    int counter = 0;
    for (int i = 0; i < N; i++) {
        if (B[i].red) counter++;
    }

    char input_str[20];
    sprintf(input_str, "%d", counter);
    char *token = strtok(input_str, " ");
    int second_result = 0;
    while (token != NULL) {
        second_result++;
        if (strcmp(token, "0") == 0) break;
        token = strtok(NULL, " ");
    }

    printf("%d\n", second_result);

    return 0;
}

