#include <stdio.h>
#include <string.h>
#include <math.h>

// Helper functions

void reverse_in_place(char* s, int len) {
    for (int i = 0; i < len/2; i++) {
        char temp = s[i];
        s[i] = s[len-1 -i];
        s[len-1 -i] = temp;
    }
}

int isLatinLetter(char c) {
    return (c >= 'a' && c <= 'z');
}

int isPunctuation(char c) {
    return c == '.' || c == ',' || c == '!' || c == '?';
}

char* process_second_part(const char* input_str) {
    int len = strlen(input_str);
    if (len == 0) return "";
    char* result = malloc(len * 2 + 1);
    int result_len = 0;
    result[result_len++] = input_str[0];
    for (int i = 1; i < len; i++) {
        char c = input_str[i];
        if (isLatinLetter(c)) {
            if (!isLatinLetter(input_str[i-1])) {
                result[result_len++] = ' ';
            }
            result[result_len++] = c;
        } else if (isPunctuation(c)) {
            result[result_len++] = c;
        }
    }
    result[result_len] = '\0';
    return result;
}

int main() {
    int N, K;
    char S[100001]; // assuming N up to 1e5
    scanf("%d %d", &N, &K);
    scanf("%s", S); // assuming the string is exactly N characters.

    char sDash[N+1];
    int firstTime = 1;
    int step = 1;

    while (K > 0) {
        // Create T as reverse of S
        char T[N+1];
        strcpy(T, S);
        reverse_in_place(T, N);

        // Create concatenated string S + T
        char concat[2*N +1];
        strcpy(concat, S);
        strcat(concat, T);

        // Reverse concat to get revU
        char revU[2*N +1];
        strcpy(revU, concat);
        reverse_in_place(revU, 2*N);

        // Initialize sDash to S
        strcpy(sDash, S);

        // Inner loop over i from N down to 0, step by step
        for (int i = N; i >=0; i -= step) {
            if (i + N > 2*N) { // check if substring is valid
                break; // this can't happen since i starts at N and step is 1 initially
            }
            char tmp[N+1];
            strncpy(tmp, revU + i, N);
            tmp[N] = '\0';
            if (strcmp(sDash, tmp) > 0) {
                strcpy(sDash, tmp);
            } else {
                if (!firstTime) {
                    break;
                }
            }
        }

        // Check firstTime condition
        if (firstTime) {
            firstTime = 0;
            if (pow(2, K) > N) {
                // Output and exit
                char first_char = sDash[0];
                for (int i =0; i < N; i++) {
                    putchar(first_char);
                }
                putchar('\n');
                return 0;
            }
        } else {
            step += step; // step *=2
        }

        // Update S to be reversed sDash
        char new_S[N+1];
        strcpy(new_S, sDash);
        reverse_in_place(new_S, N);
        strcpy(S, new_S);

        K--;
    }

    // After loop, the first part's output is sDash (since S.reverse() would be sDash)
    // Wait, no, according to earlier analysis, the first part's output is sDash.

    // Thus, pass sDash to the second part.

    char* processed = process_second_part(sDash);
    printf("%s\n", processed);
    free(processed);
    return 0;
}

