#include <stdio.h>

#include <string.h>

#include <math.h>

int main() {

    int VP, VD, T, F, C;

    scanf("%d % d % d % d % d", &VP, &VD, &T, &F, &C);

    int answer;

    if (VD <= VP) {

        answer = 0;

    } else {

        double start = T;

        answer = 0;

        while(1) {

            double x = start * VP / (VD - VP);

            if ((start + x) * VP >= C) {

                break;

            }

            start += 2 * x + F;

            answer++;

        }

    }

    // Now convert answer to string. char s[20]; // enough for integer up to 2^64, but answer is probably smaller.

