    int len = strlen(s);

    for (int i=0; i < len; i++) {

        char my = (i%2 ==0) ? 'g' : 'p';

        char his = s[i];

        if (my != his) {

            if (my == 'p') {

                score +=1;

            } else {

                score -=1;

            }

        }

    }

    printf("%d\n", score);

