integer function gcd(a, b)

    integer, intent(in) :: a, b

    integer :: tmp, r

    if (b > a) then

        tmp = a

        a = b

        b = tmp

    end if

    if (b ==0) then

        gcd = a

        return

    end if

    do

        r = mod(a, b)

        if (r ==0) then

            gcd = b

            return

        end if

        a = b

        b = r

    end do

end function gcd

