#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    // Compute first part
    int t;
    if (n % 2 == 1) {
        t = (n + 1)/2;
    } else {
        t = n/2;
    }
    double value = (double)t / n;
    // Convert to string S
    char s[100];
    sprintf(s, "%.10g", value); // Using 10 significant digits, which should match Python's output for most cases.
    // Now process S as per second program
    int len = strlen(s);
    int freq[256] = {0}; // Assuming ASCII characters
    for (int i = 0; i < len; i++) {
        freq[(unsigned char)s[i]]++;
    }
    int unique = 0;
    for (int i = 0; i < 256; i++) {
        if (freq[i] > 0) unique++;
    }
    if (unique ! = 2) {
        printf("No\n");
        return 0;
    }
    // Check each character's count is 2
    int valid = 1;
    for (int i = 0; i < 256; i++) {
        if (freq[i] ! = 0 && freq[i] ! = 2) {
            valid = 0;
            break;
        }
    } if (valid) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}

