#include <stdio.h>
#include <string.h>

#define MAX_INPUT 100000

int main() {
    char s_in[MAX_INPUT];
    fgets(s_in, MAX_INPUT, stdin);
    // Remove newline if present
    size_t len = strlen(s_in); if (len >0 && s_in[len-1] == '\n') {
        s_in[len-1] = '\0';
        len--;
    }

    // Process first step
    int res_len =0;
    char result[MAX_INPUT]; // assuming input length is up to MAX_INPUT, result can be same size. int i =0;
    while (i < len) {
        if (s_in[i] == '.') {
            result[res_len++] = '0';
            i +=1;
        } else {
            // else block
            i +=1; // first increment
            char next_char = '\0'; if (i < len) {
                next_char = s_in[i];
            }
            if (next_char == '.') {
                result[res_len++] = '1';
            } else {
                result[res_len++] = '2';
            }
            i +=1; // second increment
        }
    }
    result[res_len] = '\0'; // null terminate

    // Now process second step
    int count_even0 =0, count_even1 =0;
    int count_odd0 =0, count_odd1 =0;

    for (int j=0; j < res_len; j++) {
        char c = result[j]; if (j %2 ==0) { // even index
            if (c == '0') count_even0++;
            else if (c == '1') count_even1++;
        } else { // odd index
            if (c == '0') count_odd0++;
            else if (c == '1') count_odd1++;
        }
    }

    int option1 = res_len - (count_even0 + count_odd1);
    int option2 = res_len - (count_even1 + count_odd0);

    int min_val = (option1 < option2) ? option1 : option2;

    printf("%d\n", min_val);

