   count +=1

   if i == N-1: break

   if A[i] == A[i+1]:

       i +=1

       while i < N-1 and A[i] == A[i+1]:

           i +=1

   else if A[i] < A[i+1]:

       while i < N-1 and A[i] <= A[i+1]:

           i +=1

   else:

       while i < N-1 and A[i] >= A[i+1]:

           i +=1

   end if

end while

