// First part's data and functions. struct Point {
    int x, y, h;
};

// Function to process first problem and return the coordinates string. char* process_first(int n, struct Point points [], char *output_str) {
    // Find first point with h>0
    int x0, y0, h0;
    for (int i = 0; i < n; i++) {
        if (points[i].h > 0) {
            x0 = points[i].x;
            y0 = points[i].y;
            h0 = points[i].h;
            break;
        }
    }

    // Iterate all possible cx and cy
    for (int cx = 0; cx <= 100; cx++) {
        for (int cy = 0; cy <= 100; cy++) {
            int ch = h0 + abs(cx - x0) + abs(cy - y0);
            int valid = 1;
            for (int i = 0; i < n; i++) {
                int x = points[i].x;
                int y = points[i].y;
                int h = points[i].h;
                int dist = abs(cx - x) + abs(cy - y);
                int calc_h = ch - dist; if (calc_h < 0) calc_h = 0;
                if (calc_h ! = h) {
                    valid = 0;
                    break;
                }
            }
            if (valid) {
                // Found the candidate, format into the output string
                sprintf(output_str, "% d % d % d", cx, cy, ch);
                return output_str;
            }
        }
    }
    // Shouldn't reach here if input is valid. return "";
}

// Second part's processing: takes input string, returns the total as an integer. int process_second(const char *input_str) {
    const char *s = input_str;
    int len = strlen(s);
    char t[len + 1];
    int ti = 0;
    int i = 0;
    while (i < len) {
        if (s[i] == 'A') {
            t[ti++] = 'A';
            i++;
        } else if (s[i] == 'B') {
            if (i < len -1 && s[i+1] == 'C') {
                t[ti++] = 'D';
                i +=2;
            } else {
                t[ti++] = 'X';
                i++;
            }
        } else {
            t[ti++] = 'X';
            i++;
        }
    }
    t[ti] = '\0';

    int total = 0;
    int numA = 0;
    for (int j = 0; j < ti; j++) {
        if (t[j] == 'X') {
            numA = 0;
        } else if (t[j] == 'A') {
            numA++;
        } else { // 'D'
            total += numA;
        }
    }
    return total;
}

// Third part's processing: takes the total as a string, returns the minimal changes. int process_third(const char *input_str) {
    int len = strlen(input_str);
    char *s = (char*)malloc(len+1);
    strcpy(s, input_str);
    // even indices (0,2,4...) and odd (1,3,5...)
    int even0 =0, even1=0, odd0=0, odd1=0;
    for (int i=0; i < len; i++) {
        char c = s[i];
        if (i%2 ==0) { // even
            if (c == '0') even0++;
            else if (c == '1') even1++;
        } else { // odd
            if (c == '0') odd0++;
            else if (c == '1') odd1++;
        }
    }
    // Option1: even 0, odd 1 → changes needed: (even1) + (odd0)
    int option1 = even1 + odd0;
    // Option2: even 1, odd 0 → changes needed: (even0) + (odd1)
    int option2 = even0 + odd1;
    int min_changes = (option1 < option2) ? option1 : option2;
    free(s);
    return min_changes;
}

