#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007

struct Item {
    int S, R, H, C;
};

int main() {
    int n;
    scanf("%d", &n);
    struct Item items[n];
    for (int i=0; i <n; i++) { scanf("%d %d % d % d", &items[i].S, &items[i].R, &items[i].H, &items[i].C);
    }

    // Compute outdated array
    int outdated[n];
    memset(outdated, 0, sizeof(outdated));

    for (int i=0; i <n; i++) {
        for (int j=0; j <n; j++) {
            if (i != j) {
                if (items[i].S < items[j].S && items[i].R < items[j].R && items[i].H < items[j].H) {
                    outdated[i] =1;
                    break;
                }
            }
        }
    }

    // Find the minimum cost item not outdated
    int min_cost = 100000;
    int input_number =0;
    for (int i=0; i <n; i++) {
        if (!outdated[i] && items[i].C < min_cost) {
            min_cost = items[i].C;
            input_number = i+1; // 1-based index
        }
    }

    // Now process second part
    char s2_input[20];
    sprintf(s2_input, "%d", input_number);

    // Process second part
