#include <stdio.h>
#include <string.h>

int gcd(int a, int b) {
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

int main() {
    int x, y, a, b;
    scanf("%d %d % d % d", &x, &y, &a, &b);

    int g = gcd(x, y);
    int lcm = (x * y) / g;
    int first_result = (b / lcm) - ((a - 1) / lcm);

    char first_str[20];
    sprintf(first_str, "%d", first_result);

    // Second step processing
    int counts[256] = {0};
    for (int i = 0; first_str[i] ! = '\0'; i++) {
        counts[first_str[i]]++;
    }

    int odd_count = 0;
    for (int i = 0; i < 256; i++) {
        if (counts[i] % 2 != 0) {
            odd_count++;
        }
    }

    char second_result[8];
    if (odd_count <= 1) {
        strcpy(second_result, "First");
    } else {
        int len = strlen(first_str); if (len % 2 == 0) {
            strcpy(second_result, "Second");
        } else {
            strcpy(second_result, "First");
        }
    }

    // Third step processing
    int hasA = 0, hasB = 0;
    for (int i = 0; second_result[i] ! = '\0'; i++) {
        if (second_result[i] == 'A') hasA = 1;
        if (second_result[i] == 'B') hasB = 1;
    }

    if (hasA && hasB) {
        printf("Yes");
    } else {
        printf("No");
    }

    return 0;
