#include <stdio.h>
#include <stdlib.h>

typedef struct Edge {
    int to;
    int length;
    struct Edge *next;
} Edge;

typedef struct StackElement {
    int node;
    int sum;
    int parent;
} StackElement;

int main() {
    int n;
    scanf("%d", &n);

    Edge **adj = (Edge **)calloc(n, sizeof(Edge *));

    for (int i = 0; i < n - 1; ++i) {
        int u, v, l;
        scanf("%d %d %d", &u, &v, &l);
        u--; v--;

        Edge *e1 = (Edge *)malloc(sizeof(Edge));
        e1->to = v;
        e1->length = l;
        e1->next = adj[u];
        adj[u] = e1;

        Edge *e2 = (Edge *)malloc(sizeof(Edge));
        e2->to = u;
        e2->length = l;
        e2->next = adj[v];
        adj[v] = e2;
    }

    int *colors = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n; ++i) {
        colors[i] = -1;
    }

    StackElement *stack = (StackElement *)malloc(n * sizeof(StackElement));
    int stack_top = 0;

    stack[stack_top].node = 0;
    stack[stack_top].sum = 0;
    stack[stack_top].parent = -1;
    stack_top++;

    while (stack_top > 0) {
        stack_top--;
        int u = stack[stack_top].node;
        int sum_u = stack[stack_top].sum;
        int parent_u = stack[stack_top].parent;

        if (colors[u] != -1) continue;

        colors[u] = sum_u % 2;

        Edge *edge = adj[u];
        while (edge != NULL) {
            int v = edge->to;
            int length = edge->length;
            if (v != parent_u) {
                stack[stack_top].node = v;
                stack[stack_top].sum = sum_u + length;
                stack[stack_top].parent = u;
                stack_top++;
            }
            edge = edge->next;
        }
    }

    for (int i = 0; i < n; ++i) {
        printf("%d\n", colors[i]);
    }

    // Free allocated memory (optional for a programming contest)
    for (int i = 0; i < n; ++i) {
        Edge *edge = adj[i];
        while (edge != NULL) {
            Edge *temp = edge;
            edge = edge->next;
            free(temp);
        }
    }
    free(adj);
    free(colors);
    free(stack);

    return 0;
}

