void increment(char *num) {
    int len = strlen(num);
    int carry = 1;
    for (int i = len - 1; i >= 0 && carry; --i) {
        int digit = num[i] - '0';
        digit += carry;
        carry = digit / 10;
        num[i] = (digit % 10) + '0';
    }
    if (carry) {
        memmove(num + 1, num, len + 1);
        num[0] = '1';
    }
}

void remove_leading_zeros(char *num) {
    int len = strlen(num);
    int non_zero_index = 0;
    while (non_zero_index < len && num[non_zero_index] == '0') {
        non_zero_index++;
    }
    if (non_zero_index == len) {
        // All zeros
        num[0] = '0';
        num[1] = '\0';
    } else {
        memmove(num, num + non_zero_index, len - non_zero_index + 1);
    }
