#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int day;
    int salary;
} Job;

// Max-heap for x (based on day)
Job *x_heap = NULL;
int x_size = 0;
int x_capacity = 0;

void x_push(Job job) {
    if (x_size == x_capacity) {
        x_capacity = x_capacity == 0 ? 1 : x_capacity * 2;
        x_heap = realloc(x_heap, x_capacity * sizeof(Job));
    }
    int i = x_size++;
    x_heap[i] = job;
    while (i > 0) {
        int parent = (i - 1) / 2;
        if (x_heap[parent].day >= x_heap[i].day) break;
        Job temp = x_heap[parent];
        x_heap[parent] = x_heap[i];
        x_heap[i] = temp;
        i = parent;
    }
}

Job x_pop() {
    Job max = x_heap[0]; x_heap[0] = x_heap[--x_size];
    int i = 0;
    while (1) {
        int left = 2*i + 1;
        int right = 2*i + 2;
        int largest = i;
        if (left < x_size && x_heap[left].day > x_heap[largest].day) largest = left;
        if (right < x_size && x_heap[right].day > x_heap[largest].day) largest = right;
        if (largest == i) break;
        Job temp = x_heap[i];
        x_heap[i] = x_heap[largest];
        x_heap[largest] = temp;
        i = largest;
    }
    return max;
}

// Min-heap for y (based on salary)
int *y_heap = NULL; int y_size = 0;
int y_capacity = 0;

void y_push(int salary) {
    if (y_size == y_capacity) {
        y_capacity = y_capacity == 0 ? 1 : y_capacity * 2;
        y_heap = realloc(y_heap, y_capacity * sizeof(int));
    }
    int i = y_size++;
    y_heap[i] = salary;
    while (i > 0) {
        int parent = (i - 1)/2;
        if (y_heap[parent] <= y_heap[i]) break;
        int temp = y_heap[parent];
        y_heap[parent] = y_heap[i];
        y_heap[i] = temp;
        i = parent;
    }
}

int y_pop() {
    int min = y_heap[0];
    y_heap[0] = y_heap[--y_size];
    int i = 0;
    while (1) {
        int left = 2*i +1;
        int right = 2*i +2;
        int smallest = i;
        if (left < y_size && y_heap[left] < y_heap[smallest]) smallest = left;
        if (right < y_size && y_heap[right] < y_heap[smallest]) smallest = right;
        if (smallest == i) break;
        int temp = y_heap[i];
        y_heap[i] = y_heap[smallest];
        y_heap[smallest] = temp;
        i = smallest;
    }
    return min;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    for (int i = 0; i < n; i++) {
        int day, salary;
        scanf("%d %d", &day, &salary);
        Job job = {day, salary};
        x_push(job);
    }

    while (x_size > 0) {
        Job job = x_pop();
        int r = m - y_size; if (r >= job.day) {
            y_push(job.salary);
        } else {
            y_push(job.salary);
            y_pop();
        }
    }

    int sum = 0;
    for (int i = 0; i < y_size; i++) {
        sum += y_heap[i];
    }
    printf("%d\n", sum);

    free(x_heap);
    free(y_heap);
    return 0;
}

