            index = N-1;
            while (index >= 0) {
                while (top ! = -1 && arr[stack[top]] > arr[index]) {
                    int ind = stack[top--];
                    leftl[ind] = ind - index - 1;
                }
                stack[++top] = index--;
            }
            while (top ! = -1) {
                int ind = stack[top--];
                leftl[ind] = ind; // because index is now -1: so ind - (-1) - 1 = ind.
            }

            long long total = 0;
            for (int i=0; i<N; i++) {
                total += (long long)arr[i] * (leftl[i]+1) * (rightl[i]+1);
            }
            printf("%lld\n", total);

            // Free the rest
            free(arr);
            free(leftl);
            free(rightl);
            free(stack);

            return 0;
        }

