          int sign = (a[i] > 0) - (a[i] < 0); if (abs_val > max_val) {
              max_val = abs_val;
              max_sign = sign;
              max_index = i;
          }
      }

      if (max_sign == 0) {
          printf("0\n");
      } else {
          printf("%d\n", 2*N-1);
