   Summary of the C program:

        #include <stdio.h>
        #include <string.h>
        #include <stdlib.h>

        int G(int a, int b) {
            while (a % b != 0) {
                int temp = b;
                b = a % b;
                a = temp;
            }
            return b;
        }

        int L(int a, int b) {
            return a * b / G(a, b);
        }

        int main() {
            char line[1000];
            // Read the first line of input
            fgets(line, sizeof(line), stdin);
            int x, y, a, b;
            sscanf(line, "% d % d % d % d", &x, &y, &a, &b);

            // Step 1: Compute the first program's result
            int lcm = L(x, y);
            int res1 = b / lcm - (a - 1) / lcm;

            // Convert res1 to a string
            char str[20];
            sprintf(str, "%d", res1);

            // Now, run the second program on the string `str`
            int num_g = 0, num_p = 0, score = 0;
            int len = strlen(str);
            for (int i = 0; i < len; i++) {
                char c = str[i];
                if (c == 'g') {
                    if (num_g - num_p > 0) {
                        score += 1;
                        num_p += 1;
                    } else {
                        num_g += 1;
                    }
                } else if (c == 'p') {
                    if (num_g - num_p > 0) {
                        num_p += 1;
                    } else {
                        score -= 1;
                        num_g += 1;
                    }
                }
                // Note: if the character is not 'g' or 'p', we do nothing (as in the Python code)
            }

            // Output the score (from the second program)
            printf("%d\n", score);

            return 0;
        }

