#include <stdio.h>
#include <ctype.h>
#include <string.h>

int main() {
    // Step 1: Read T, S, q (three integers)
    int T, S, q; scanf("%d %d %d", &T, &S, &q);

    // Step 2: Compute n (the answer from the first program)
    long previous = S;
    int answer = 0;
    while (previous < T) {
        answer++;
        previous *= q;
    }
    int n = answer;

    // Step 3: Convert n to a string and take the first character
    char str[20]; // enough for an integer
    sprintf(str, "%d", n);
    char ch1 = str[0];
