#include <bits/stdc++.h>
using namespace std;

int main() {
    int t;
    cin >> t;
    while(t--) {
        int m;
        cin >> m;
        vector<int> a(m);
        for (int i = 0; i < m; i++) {
            cin >> a[i];
        }
        int ans = 0;
        while (a.size() != 1) {
            vector<int> b;
            for (int i = 0; i < a.size(); i += 2) {
                if (a[i] > a[i + 1]) {
                    ans += 1;
                }
                b.push_back(min(a[i], a[i + 1]));
            }
            a = b;
        }
        cout << ans << endl;
    }
