                    #include <bits/stdc++.h>
                    using namespace std;
                    typedef long long ll;
                    int a[5000], n;
                    ll solve(int i) {
                        ll cur = 0, ans = 0;
                        for (int j=i+1; j < n; j++) {
                            ans += (cur / a[j] + 1);
                            cur = a[j] * (ll)(cur / a[j] + 1);
                        }
                        cur = 0;
                        for (int j=i-1; j>=0; j--) {
                            ans += (cur / a[j] + 1);
                            cur = a[j] * (ll)(cur / a[j] + 1);
                        }
                        return ans;
                    }
                    int main() {
                        cin >> n;
                        for (int i=0; i<n; i++) cin >> a[i];
                        ll ans = 1e18;
                        for (int i=0; i<n; i++) {
                            ans = min(ans, solve(i));
                        }
