   #include <bits/stdc++.h>
   using namespace std;
   int n;
   vector<int> b;
   vector<int> ans;

   int main() {
     ios_base::sync_with_stdio(false);
     cin >> n;
     b.resize(n);
     for (int i = 0; i < n; i++) {
       cin >> b[i];
     }
     for (int i = 0; i < 19; i++) {
       for (int j = 0; j < n; j++) {
         if (j & (1 << i)) {
           b[j] ^= b[j ^ (1<<i)];
         }
       }
     }
     ans.resize(n);
     for (int i = 0; i < n; i++) {
       ans[i] = b[n-1-i];
     }
     for (int i = 0; i < n; i++) {
       cout << ans[i] << " ";
     }
     cout << endl;
   }

