    int[] a = new int[n]; 
    for (int i = 0; i < n; i++) 
    { 
        int current = 0; 
        foreach (var c in allCons[i]) 
        { 
            if (c.R != i) continue; 
            int other = c.L; 
            int x = c.Value; 
            int required = (x & ~a[other]); 
            if (required > current) 
                current = required; 
        } 
        a[i] = current & max_bits[i]; 
    } 
    Console.WriteLine(string.Join(" ", a)); 
}
