import java.io. InputStreamReader;
import java.util. *;

public class Main {
    static Scanner cin = new Scanner(new InputStreamReader(System.in));

    public static void main(String[] args) {
        int t = cin.nextInt();
        while (t-->0) {
            solve();
        }
        cin.close();
    }

    private static void solve() {
        int n = cin.nextInt();
        long sum = 0;
        String ans = "";
        for (int i = 1; i <= n; i++) {
            long temp = cin.nextLong();
            if (!ans.isEmpty()) {
                continue;
            }
            sum += temp;
            if (i > 1) {
                if (sum == 0 && temp ! = 0) {
                    ans = "No";
                }
            }
        } if (ans.isEmpty()) {
            if (sum == 0) {
                ans = "Yes";
            } else {
                ans = "No";
            }
        }
        System.out.println(ans);
    }
}
