#include "bits/stdc++.h"
using namespace std;
#define int long long
#define pb push_back
#define all(x) (x).begin(), (x).end()

void solve() {
    int n; cin >> n;
    string s; cin >> s;
    s.pb('W');
    vector<string> tokens;
    stringstream check(s);
    while(getline(check, word, 'W')) {
        tokens.pb(word);
    }
    for(auto x : tokens) {
        if(x.size() % 2 !=0) return void(cout << "NO\n");
        if(count(all(x), 'R') == 0 or count(all(x), 'B') == 0) return void(cout << "NO\n");
    }
    cout << "YES\n";
}

int32_t main() {
    cin.tie(0)->sync_with_stdio(0);
    int T = 1; cin >> T;
    while (T--) solve();
}
