MOD = 998244353

def modmul(x, y, c = 0):
    return (x * y + c) % MOD

def inv(x):
    return pow(x, MOD-2, MOD)

MAX = 10 ** 6

fact = [1]
for i in range(1, MAX):
    fact.append(modmul(i, fact[i-1]))

invfact = [1] * (MAX)
for i in range(MAX - 1, -1, -1):
    invfact[i] = modmul(invfact[i+1], invfact[i])

def comb(x, y):
    return modmul(fact[x], invfact[y])

def invcomb(x, y):
    return modmul(invfact[x], invfact[y])

def invs(x):
    return modmul(fact[x-1], invfact[x])

n = int(input())

out = [0] * n

for i in range((n + 1) // 2):
    base = fact[n - 1]
    frac = modmul(comb(n//2, i), invcomb(n - 1, i))

    out[i] = modmul(base, frac)

rem = 0   
for i in range(n - 1, -1, -1):
    oldrem = rem
    rem = modmul(rem, invs(i))
    out[i] -= oldrem

    rem %= MOD
    out[i] %= MOD

print(' '.join(map(str,out)))

